import java.util.*;public class ReverseWords{ public static String reverseWords(String str){    String words[]=str.split("\\s");         String reverseWords = "";         for (int j = words.length -1; j >= 0; j--){      StringBuilder sb = new StringBuilder(words[j]);      reverseWords += sb.toString() + " ";     }       StringBuilder sb = new StringBuilder(reverseWords);       sb = sb.deleteCharAt(sb.length() - 1);       return sb.toString(); }  }